
public class Reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int Arr[]= {3,9,1,2,4,7};
	
	for( int i=0;i<Arr.length;i++)
	{
		System.out.println(Arr[i]);
		
	}
	System.out.println("--------------------");
	for( int i=Arr.length-1;i>=0;i--)
	{
		System.out.println(Arr[i]);
		
	}
	}

}
